package socket.nginx;import java.io.FileInputStream;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import java.util.Properties;/** *  */public class PropertiesUtil {	private static Properties prop;		static{		InputStream in = null;        prop = new Properties();        try {        	String path = System.getProperty("user.dir") + "\\config.properties";            in = new FileInputStream(path);            prop.load(new InputStreamReader(in, "utf-8"));        }        catch (Exception e) {            e.printStackTrace();        }        finally {            if (in != null) {                try {                    in.close();                }                catch (IOException e) {                    e.printStackTrace();                }            }        }	}		public static String getParam(String key){		return prop.getProperty(key);	}}